        8495653923123431417604973247489272438418190587263600148770280649306958101930
      ]
    );
    vk.delta2 = ValidDocumentIdPairing.G2Point(
      [
        18350877368776754025045685187716627854369653780936510500750914213809605525187,
        6254600562700475972460121329686865023942695733510697900256506934187977622723
      ],
      [
        5496349254407405065987036505581034067249875410365809092957861832107783124915,
        7155757018160691671552046106243095634252700481764353941962088045871467175852
      ]
    );
    vk.IC = new ValidDocumentIdPairing.G1Point[](2);

    vk.IC[0] = ValidDocumentIdPairing.G1Point(
      16343576977154026618079659125899271337316365385182419844778711316436831122709,
      17061378451226668405972122945551787391401733398741683636278318813164040200354
    );

    vk.IC[1] = ValidDocumentIdPairing.G1Point(
      7929960780092852689646911121604575205411898629951606630075450471533132018173,
      11962554901184474316679526381223715652492322526776074470997200466548379083577
    );
  }

  function verify(
    uint[] memory input,
    Proof memory proof
  ) internal view returns (uint) {
    uint256 snark_scalar_field = 21888242871839275222246405745257275088548364400416034343698204186575808495617;
    VerifyingKey memory vk = verifyingKey();
    require(input.length + 1 == vk.IC.length, "verifier-bad-input");
    // Compute the linear combination vk_x
    ValidDocumentIdPairing.G1Point memory vk_x = ValidDocumentIdPairing.G1Point(
      0,
      0
    );
    for (uint i = 0; i < input.length; i++) {
      require(input[i] < snark_scalar_field, "verifier-gte-snark-scalar-field");
      vk_x = ValidDocumentIdPairing.addition(
        vk_x,
        ValidDocumentIdPairing.scalar_mul(vk.IC[i + 1], input[i])
      );
    }
    vk_x = ValidDocumentIdPairing.addition(vk_x, vk.IC[0]);
    if (
      !ValidDocumentIdPairing.pairingProd4(
        ValidDocumentIdPairing.negate(proof.A),
        proof.B,
        vk.alfa1,
        vk.beta2,
        vk_x,
        vk.gamma2,
        proof.C,
        vk.delta2
      )
    ) return 1;
    return 0;
  }

  /// @return r  bool true if proof is valid
  function verifyProof(
    uint[2] memory a,
    uint[2][2] memory b,
    uint[2] memory c,
    uint[1] memory input
  ) public view returns (bool r) {
    Proof memory proof;
    proof.A = ValidDocumentIdPairing.G1Point(a[0], a[1]);
    proof.B = ValidDocumentIdPairing.G2Point(
      [b[0][0], b[0][1]],
      [b[1][0], b[1][1]]
    );
    proof.C = ValidDocumentIdPairing.G1Point(c[0], c[1]);
    uint[] memory inputValues = new uint[](input.length);
    for (uint i = 0; i < input.length; i++) {
      inputValues[i] = input[i];
    }
    if (verify(inputValues, proof) == 0) {
      return true;
    } else {
      return false;
    }
  }
}

